import React from "react";
import '../App.css';
import * as yup from "yup";



const userSchema = yup.object().shape({
    name: yup.string().required("Name is required"),
    email:yup.string().email().required("Please enter valid email"), 
    password: yup.string().min(4).max(10).required("Password is required"),
})

function SignUp(){

    const createUser = async (e) => {
        e.preventDefault()
        let formData = {
            name: e.target[0].value,
            email: e.target[1].value,
            password: e.target[2].value,
        }
        console.log(formData)
        const isValid = await userSchema.isValid(formData)
        console.log(isValid)
    }

    return (
        <>
        <div className="flex flex-wrap min-h-full pt-16">
        <div className="">
            <img className="object-cover" src="https://malihomesolutions.co.ke/wp-content/uploads/2021/03/Kikuyu-Grass.jpg" alt="Sign Up"/>
        </div>
        <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 ">
            <h1 className="text-center leading-5 text-xs">Sign Up</h1>
            <p className="pl-44 font-serif font-normal">The start of something new</p>

            <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
                <form 
                    className="m-0 flex flex-col space-y-10" 
                    onSubmit={createUser} 
                    autoComplete="off"
                    >
                    <label>First Name</label><input type="text" placeholder="John"/>
                    <label>Last Name</label><input type="text" placeholder="Doe"/>
                    <label>Email</label><input type="text" placeholder="johndoe@gmail.com"/>
                    <label>Password</label><input type="text" placeholder="password"/>
                    <button className="bg-[#235F97] flex w-full justify-center rounded-md px-3 p-2.5 text-base font-semibold leading-6 text-white shadow-sm hover:bg-indigo-900 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600" type="submit">SIGN UP</button>
                </form>
            </div>  
        </div>
        </div>
        </>
    )
}

export default SignUp
